bool find(int K,int B[],int cnt,int T)
{ 
    if(K == B[K])
    { 
      	if(cnt<T)
      	return true;
      	else
      	return false;
	}
    if(cnt>=T)
    return false;

    return find(B[K],B,cnt+1,T);
}
int doubtSolver(int n,int A[],int B[],int K,int T)
{  
	bool res=find(K,B,1,T);
	if(res==true)
	return 1;
	else
	return -1;
}

